import React from 'react'

const Select = ({ data, name, label, error, ...rest }) => {
    console.log(data)
    return ( 
        <div className="form-group">
            <label htmlFor={ name }> { label }</label>
            <select 
                {...rest}
                className="form-control" 
                id={ name } 
                name={ name }
            >
                { data.genre &&
                    data.genre.map( item => console.log(item) || <option key={item._id} value={item.name} >{ item.name }</option>)
                }
            </select>    

            { error && <div className='alert alert-danger'>{ error }</div> }

        </div> 
    )
}

export default Select
